import React from 'react';
import 'tachyons';
import './BlogCardGrid.css';
import axios from 'axios';


class BlogCard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      expanded: false,
      visible: false,
      isEditMode: false,
      title: '',
      intro: '',
      description: '',
    };
  }

  expandBlogPost = () => {
    this.setState({
      expanded: !this.state.expanded,
    });
  }

  deleteBlogPost = () => {
    this.setState({
      visible: false,
    })
  }

  editBlogPost = () => {
    this.setState({
      isEditMode: true,
      title: '',
    });
    console.log(this.props.intro);
  }

  onChangeTitle = (event) => {
    this.setState({
      title: event.target.value,
    });
  }

  onChangeIntro = (event) => {
    this.setState({
      intro: event.target.value,
    });
  }

  onChangeDescription = (event) => {
    this.setState({
      description: event.target.value,
    });
  }


  render() {
    const {title, intro, description} = this.state;
    if (!this.state.isEditMode) {
      return (
        <div className="child center bg-washed-yellow shadow-5 mw6-ns mw5 br3 hidden ba b--black-10 mv4">
          <h1 className="f4 br3 br--top dark-blue mv0 pv2 ph3">{this.props.title}</h1>
          <div className="pa3 bt b--black-10">
            <p className="f6 f5-ns lh-copy measure blue">
              {this.props.intro}
            </p>
            {this.state.expanded
              ? <div>
                  <p className="f6 f5-ns lh-copy measure blue">
                    {this.props.content}
                  </p>
                  <span className="f5-ns f7  dib blue bg-animate hover-bg-washed-red no-underline pv2 ph4 br-pill ba b--blue-20 ma2-ns ma1 pointer" onClick={this.expandBlogPost}>Read Less </span>
                  <span className="f5-ns f7  dib blue bg-animate hover-bg-washed-red no-underline pv2 ph4 br-pill ba b--blue-20 ma2-ns ma1 pointer" onClick={this.deleteBlogPost}> Delete </span>
                  <span className="f5-ns f7  dib blue bg-animate hover-bg-washed-red no-underline pv2 ph4 br-pill ba b--blue-20 ma2-ns ma1 pointer" onClick={this.editBlogPost}> Edit </span>
                </div>
              : <div>
                  <span className="f5-ns f7  dib blue bg-animate hover-bg-washed-red no-underline pv2 ph4 br-pill ba b--blue-20 ma2-ns ma1 pointer" onClick={this.expandBlogPost}>Read More </span>
                  <span className="f5-ns f7  dib blue bg-animate hover-bg-washed-red no-underline pv2 ph4 br-pill ba b--blue-20 ma2-ns ma1 pointer" onClick={this.deleteBlogPost}> Delete </span>
                  <span className="f5-ns f7  dib blue bg-animate hover-bg-washed-red no-underline pv2 ph4 br-pill ba b--blue-20 ma2-ns ma1 pointer" onClick={this.editBlogPost}> Edit</span>
                </div>
            }
          </div>
        </div>
      );
    }
    else {
      return( 
      <div className="ba2 br2 ma3 shadow-5 mw8 center bg-washed-yellow">
        <main className="pa4 black-80">
          <form className="measure center">
            <fieldset className="ba b--transparent ph0 mh0">
              <legend className="dark-blue f4 fw6 ph0 mh0">Create a Blog</legend>
              <div className="mt3">
                <div className="db fw6 lh-copy f6 blue">Title</div>
                <input style={{ outline: 'none' }} onChange={this.onChangeTitle} value={title} className="pa2 input-reset ba br2 b--washed-red bg-transparent hover-bg-washed-red hover-blue w-100" type="text" />
              </div>
              <div className="mt3">
                <div className="db fw6 lh-copy f6 blue">Introduction </div>
                <textarea value={intro} style={{ height: '200px', outline: 'none' }} onChange={this.onChangeIntro} className="db bg-transparent ba b--washed-red hover-bg-washed-red hover-blue border-box w-100 measure ba b--black-20 pa2 br2 mb2" />
              </div>
              <div className="mt3">
                <div className="db fw6 lh-copy f6 blue">Description </div>
                <textarea value={description} style={{ height: '500px', outline: 'none' }} onChange={this.onChangeDescription} className="db bg-transparent ba b--washed-red hover-bg-washed-red hover-blue border-box w-100 measure ba b--black-20 pa2 br2 mb2" />
              </div>
            </fieldset>
            <div className="" style={{ display: 'flex', justifyContent: 'center' }}>
              <span className="f2 pointer dib blue bg-animate hover-bg-washed-red no-underline pv2 ph4 br-pill ba b--blue-20 ma2-ns ma1" onClick={this.onClickPost}>Post</span>
            </div>
          </form>
        </main>
      </div>
    );
    }

  }
}


export default BlogCard;
