import React from 'react';
import 'tachyons';
import './BlogCardGrid.css';


class BlogCard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      expanded: false,
      visible: true,
    };
  }

  expandBlogPost = () => {
    this.setState({
      expanded: !this.state.expanded,
    });
  }

  deleteBlogPost = () => {
    this.setState({
      visible: false,
    })
  }

  render() {
    return (
      <div className="child center bg-washed-yellow shadow-5 mw5 mw6-ns br3 hidden ba b--black-10 mv4">
        <h1 className="f4 br3 br--top dark-blue mv0 pv2 ph3">{this.props.title}</h1>
        <div className="pa3 bt b--black-10">
          <p className="f6 f5-ns lh-copy measure blue">
            {this.props.intro}
          </p>
          {this.state.expanded
            ? <div>
                <p className="f6 f5-ns lh-copy measure blue">
                  {this.props.content}
                </p>
                <button type="button" onClick={this.expandBlogPost}>Read Less </button>
                <button type="button" onClick={this.deleteBlogPost}> Delete Post </button>
              </div>
            : <div>
                <button type="button" onClick={this.expandBlogPost}>Read More </button>
                <button type="button" onClick={this.deleteBlogPost}>Delete Post </button>
              </div>
          }
        </div>
      </div>
    );
  }
}


export default BlogCard;
