import React from 'react';
import 'tachyons';

class Create extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: '',
      intro: '',
      description: '',
    };
  }

  onChangeTitle = (event) => {
    this.setState({
      title: event.target.value,
    });
  }
  onChangeIntro = (event) => {
    this.setState({
      intro: event.target.value,
    });
  }
  onChangeDescription = (event) => {
    this.setState({
      description: event.target.value,
    });
  }
  onClickPost = (event) => {
    console.log(this.state.title);
    console.log(this.state.intro);
    console.log(this.state.description);
  }

  render() {
    return (
      <div className="ba2 br2 ma3 shadow-5 mw8 center bg-washed-yellow">
        <main className="pa4 black-80">
          <form className="measure center">
            <fieldset className="ba b--transparent ph0 mh0">
              <legend className="dark-blue f4 fw6 ph0 mh0">Create a Blog</legend>
              <div className="mt3">
                <label className="db fw6 lh-copy f6 blue">Title</label>
                <input onChange={onChangeTitle} className="pa2 input-reset ba br2 b--washed-red bg-transparent hover-bg-washed-red hover-blue w-100" type="text" />
              </div>
              <div className="mt3">
                <label className="db fw6 lh-copy f6 blue">Introduction </label>
                <textarea style={{height: "200px"}} onChange={onChangeIntro} className="db bg-transparent ba b--washed-red hover-bg-washed-red hover-blue border-box w-100 measure ba b--black-20 pa2 br2 mb2"></textarea>
              </div>
              <div className="mt3">
                <label className="db fw6 lh-copy f6 blue">Description </label>
                <textarea style={{height: "500px"}} onChange={onChangeDescription} className="db bg-transparent ba b--washed-red hover-bg-washed-red hover-blue border-box w-100 measure ba b--black-20 pa2 br2 mb2"></textarea>
              </div>
            </fieldset>
            <div className="" style={{display:"flex", justifyContent:"center"}}>
              <input className="b ph3 br3 pv2 input-reset ba b--washed-red bg-transparent hover-bg-washed-red blue grow pointer f2 dib" type="submit" value="Post" />
            </div>
          </form>
        </main>
      </div>
    );
  }
}


export default Create;
