import React from 'react';
import 'tachyons';


class BlogCard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      expanded: false,
    };
  }

  expandBlogPost = () => {
    this.setState({
      expanded: !this.state.expanded,
    });
  }

  render() {
    return (
      <article className="center bg-washed-yellow shadow-5 mw5 mw6-ns br3 hidden ba b--black-10 mv4">
        <h1 className="f4 br3 br--top dark-blue mv0 pv2 ph3">Title of card</h1>
        <div className="pa3 bt b--black-10">
          <p className="f6 f5-ns lh-copy measure blue">
            Lorem ipsum dolor sit amet, consetetur sadipscing elitr, sed diam nonumy eirmod
            tempor invidunt ut labore et dolore magna aliquyam erat, sed diam voluptua. At
            vero eos et accusam et justo duo dolores et ea rebum.
          </p>
          {this.state.expanded
            ? <div>
                <p className="f6 f5-ns lh-copy measure blue">
                  Lorem ipsum dolor sit amet, consetetur sadipscing elitr, sed diam nonumy eirmod
                  tempor invidunt ut labore et dolore magna aliquyam erat, sed diam voluptua. At
                  vero eos et accusam et justo duo dolores et ea rebum.
                </p>
                <button type="button" onClick={this.expandBlogPost}>Read Less </button>
              </div>
            : <button type="button" onClick={this.expandBlogPost}>Read More </button>
          }
        </div>
      </article>
    );
  }
}


export default BlogCard;
