import React from 'react';
import axios from 'axios';
import BlogCard from './BlogCard';
import './BlogCardGrid.css';
import 'tachyons';

class BlogCardGrid extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      blogData: [],
    };
  }

  componentDidMount() {
    axios.get('http://localhost:4000/blogs/')
      .then(res => this.setState({
        blogData: res.data,
      }))
      .catch(err => {
        console.log(err);
      })
  }
  componentDidUpdate() {
    axios.get('http://localhost:4000/blogs/')
      .then(res => this.setState({
        blogData: res.data,
      }))
      .catch(err => {
        console.log(err);
      })
  }

  render() {
    let blogData = this.state.blogData;
    const blogDataArray = blogData.map((post, index) => (
      <BlogCard key={index} id={post._id} intro={post.intro} content={post.description} title={post.title} />
    ));
    return (
      <div className="container">
        {blogDataArray}
      </div>
    );
  }
}

export default BlogCardGrid;
